mapper.select:

         select * from modules where ParentId in 
         (select c.Id from roles a inner join rolemodules b on a.Id=b.RoleId inner join modules c on b.ModuleId=c.Id where 
           
             a.Name= #{item}
           )
           union 
          select c.* from roles a inner join rolemodules b on a.Id=b.RoleId
           inner join modules c on b.ModuleId=c.Id where 
           
             a.Name= #{item}
